package model;

public class CauHoi {
	String macauhoi;
	String noidungcauhoi;
	String luachon1;
	String luachon2;
	String luachon3;
	String luachon4;
	String dapan;
	
	public CauHoi() {
		super();
		// TODO Auto-generated constructor stub
	}
	
	public CauHoi(String macauhoi, String noidungcauhoi, String luachon1, String luachon2, String luachon3,
			String luachon4, String dapan) {
		super();
		this.macauhoi = macauhoi;
		this.noidungcauhoi = noidungcauhoi;
		this.luachon1 = luachon1;
		this.luachon2 = luachon2;
		this.luachon3 = luachon3;
		this.luachon4 = luachon4;
		this.dapan = dapan;
	}
	
	public String getMacauhoi() {
		return macauhoi;
	}
	public void setMacauhoi(String macauhoi) {
		this.macauhoi = macauhoi;
	}
	public String getNoidungcauhoi() {
		return noidungcauhoi;
	}
	public void setNoidungcauhoi(String noidungcauhoi) {
		this.noidungcauhoi = noidungcauhoi;
	}
	public String getLuachon1() {
		return luachon1;
	}
	public void setLuachon1(String luachon1) {
		this.luachon1 = luachon1;
	}
	public String getLuachon2() {
		return luachon2;
	}
	public void setLuachon2(String luachon2) {
		this.luachon2 = luachon2;
	}
	public String getLuachon3() {
		return luachon3;
	}
	public void setLuachon3(String luachon3) {
		this.luachon3 = luachon3;
	}
	public String getLuachon4() {
		return luachon4;
	}
	public void setLuachon4(String luachon4) {
		this.luachon4 = luachon4;
	}
	public String getDapan() {
		return dapan;
	}
	public void setDapan(String dapan) {
		this.dapan = dapan;
	}
	

}
